            const time1 = new Date();
            await func(this, arg1, arg2, arg3);

            const time2 = new Date();
            const elapsedTime = time2.getTime() - time1.getTime();
            const timeLeft = Math.max(6000 - elapsedTime);
            setTimeout(this.showContents, timeLeft);
